import React, { useEffect, useState } from 'react';
import './style.css'
import Navbar from '../Navbar/Navbar';
import { food, Halls, } from '../Extras/check';
import { useDispatch } from 'react-redux';
import { sendItem } from '../../redux/action';
import Message from '../Extras/Message';

function Order(props) {
    const [state, setstate] = useState('')
    const [tops, settops] = useState([
        { top: "Meat", price: 100, number: 0, newprice: 0 },
        { top: "Rounded fish", price: 200, number: 0, newprice: 0 },
        { top: "Titus Fish", price: 200, number: 0, newprice: 0 },
        { top: "Egg", price: 70, number: 0, newprice: 0 },
        { top: "Kpomo", price: 50, number: 0, newprice: 0 }
    ])
    const dispatch = useDispatch()
    // const send = useSelector(state => state.sendItem)
    const [msg, setmsg] = useState("")
    const [Hall, setHall] = useState("")
    const [quantity, setquantity] = useState(250)
    var current = new Date();
    useEffect(() => {
        if (quantity < 250 || quantity > 500) {
            setquantity(quantity)
        }
    }, [quantity])
    const writ = (e) => {
        setstate(state => ({ ...state, [e.target.name]: e.target.value }))
    }
    const in_ds = (sign, i) => {
        let newArr = [...tops]
        if (newArr[i].number < 10) {
            if (sign === "+") {
                newArr[i].number = newArr[i].number + 1
                newArr[i].newprice = newArr[i].price * newArr[i].number;
            } else {
                newArr[i].number = newArr[i].number - 1
                newArr[i].newprice = newArr[i].price * newArr[i].number;
            }
            settops(newArr)
        }
    }
    const submit = () => {

        const { Name, Room, Phone, Hall, Food } = state
        console.log(state);
        let condition = (Name.trim() === '' || Phone.trim() === '' || Hall.trim() === '' || Room.trim() === '' || Food.trim() === '');
        alert(condition)
        if (condition) {
            setmsg(<Message message="Please fill in the fields" status="error" />)
        } else {
            setmsg(<Message message="Order added to bag" status="status" />)
            let onTop = tops.filter(x => (x.number !== 0));
            let keys = ["Date", "Time", "PriceofFood", "addons"]; let values = [current.toLocaleTimeString(), current.toLocaleDateString(), quantity, onTop]
            for (let i = 0; i < keys.length; i++) {
                alert(keys[i] + " : " + values[i])
                setstate(state => ({ ...state, [keys[i]]: values[i] }))                
            }
            dispatch(sendItem(state))
            window.location.assign("/confirm")
        }
    }
    return (
        <React.Fragment>
            <Navbar />
            <div className="container position-relative" style={{ top: "15vh" }}>
                <div action="" className="col-lg-6 m-auto p-2 border">
                    <div className="h3 text-center">Complete this form to place your order {state.Date}  {state.Time} </div>
                    <div className="">
                        Make orders with us and get your order under 1 hour
                    </div>
                    <div className="">{msg}</div>
                    <div className="">
                        <input type="text" className="input my-2 rounded py-1 px-2 border" placeholder="Name" value={state.Name} name="Name" onChange={(e) => { writ(e) }} />
                    </div>
                    <div className="">
                        <input type="tel" className="input my-2 rounded py-1 px-2 border" placeholder="Phone" value={state.Phone} name="Phone" onChange={(e) => { writ(e) }} />
                    </div>
                    <div>
                        <input type="number" className="input my-2 rounded py-1 px-2 border" max="500" placeholder="Room Number" value={state.Room} name="Room" onChange={(e) => { writ(e) }} />
                    </div>

                    {/* <select placeholder="Hall" value={state.Hall} name="Hall" className="input-s my-2" onChange={(e) => { writ(e); alert(state.Hall) }} > */}
                        {/* <option value="" selected>--Select hall--</option> */}
                        {Halls.map(x => {
                            return(
                                <div onClick={()=>{ setHall(x)}} style={{cursor:"pointer"}}>{x}  Hall </div>
                            )
                        })}
                    {/* </select> */}
                    <div className="d-flex">
                        <select placeholder="Food" value={state.Food} name="Food" className="border my-2" onChange={(e) => { writ(e); ; alert(state.Food) }} style={{ height: "30px" }} >
                            <option value="" selected>--Select food--</option>
                            {food.map(x => {
                                return <option value={x}>{x}</option>
                            })}
                        </select>
                        <div className="border m-2  d-flex" style={{ height: "30px" }}>
                            <div className="px-2 h-100" style={{ cursor: 'pointer' }} onClick={() => {
                                if (quantity > 250) {
                                    setquantity(quantity - 50)
                                }
                            }}>-</div>
                            <div className="px-1">
                                {quantity}
                            </div>
                            <div className="px-2 h-100" style={{ cursor: 'pointer' }} onClick={() => {
                                if (quantity < 600) {
                                    setquantity(quantity + 50)
                                }
                            }}>+</div>
                        </div>
                    </div>
                    <div>
                        {tops.map(({ top, price, number, newprice }, i) => {
                            return (
                                <div className="d-flex mt-1 ">
                                    <div>{top} &#x20A6;{price} | &#x20A6;{newprice} </div>
                                    <div className="px-2 border ml-1" style={{ cursor: "pointer" }} onClick={() => { in_ds("-", i) }}>-</div>
                                    <div className='border-top border-bottom px-2'>{number}</div>
                                    <div className="px-2 border mr-1" style={{ cursor: "pointer" }} onClick={() => { in_ds("+", i) }}>+</div>
                                </div>
                            )
                        })}
                    </div>
                    <div style={{ width: "100%", textAlign: "center" }}>
                        <button className="btn border" onClick={() => { submit() }}> Place Order</button>
                    </div>
                </div>
            </div>
        </React.Fragment>
    );
}
export default Order;
