import React, { useEffect, useState } from 'react';
import './style.css'
import Navbar from '../Navbar/Navbar';
import { chk, food, toppings } from '../Extras/check';

function Order(props) {
    const [state, setstate] = useState({})
    const [msg, setmsg] = useState("")
    const [quantity, setquantity] = useState(250)
    var current = new Date();
    useEffect(() => {
        if (quantity < 250 || quantity > 500) {
            setquantity(quantity)
        }
    }, [quantity])
    const writ = (e) => {
        setstate(state => ({ ...state, [e.target.name]: e.target.value }))
    }

    const submit = () => {
        setstate(state => ({ ...state, Date: current.toLocaleDateString() }))
        setstate(state => ({ ...state, Time: current.toLocaleTimeString() }))
        if (state.Room > 120 || state.Room < 1) setstate(state => ({ ...state, Room: "" }))
        if (chk(state.Name, '')) {
            setstate(state => ({ ...state, Room: "" }))
        }
        fetch('http://localhost:3000/order', {
            method: "post",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ state: state })
        })
            .then(res => res.json())
            .then(res => { })
    }
    return (
        <React.Fragment>
            <Navbar />
            <div className="container position-relative" style={{ top: "20vh" }}>
                <div action="" className="col-lg-6 m-auto p-2 border">
                    <div className="h3 text-center">Complete this form to place your order {state.Date}  {state.Time} </div>
                    <div className="">
                        Make orders with us and get your order under 1 hour
                    </div>
                    <div className="">{msg}</div>
                    <div className="">
                        <input type="text" className="input my-2 rounded py-1 px-2 border" placeholder="Name" value={state.Name} name="Name" onChange={(e) => { writ(e) }} />
                    </div>
                    <div className="">
                        <input type="tel" className="input my-2 rounded py-1 px-2 border" placeholder="Phone" value={state.Phone} name="Phone" onChange={(e) => { writ(e) }} />
                    </div>
                    <div>
                        <input type="number" className="input my-2 rounded py-1 px-2 border" placeholder="Room Number" value={state.Room} name="Room" onChange={(e) => { writ(e) }} />
                    </div>
                    {/* <div className="">
                        <input type="text" className="input my-2 rounded py-1 px-2 border" placeholder="Food" value={state.Food} name="Food" onChange={(e) => { writ(e) }} />
                    </div> */}
                    <select placeholder="Hall" value={state.Hall} name="Hall" className="input-s my-2" onChange={(e) => { writ(e) }} >
                        <option value="">--Select hall--</option>
                        <option value="Caleb Hall">Caleb Hall</option>
                        <option value="Joseph Hall">Joseph Hall</option>
                        <option value="Mary Hall">Mary Hall</option>
                        <option value="Rebecca Hall">Rebecca Hall</option>
                    </select>
                    <div className="d-flex">
                        <select placeholder="Food" value={state.Food} name="Food" className="border my-2" onChange={(e) => { writ(e) }} style={{ height: "30px" }} >
                            <option value="">--Select food--</option>
                            {food.map(x => {
                                return <option value={x}>{x}</option>
                            })}
                        </select>
                        <div className="border m-2  d-flex" style={{ height: "30px" }}>
                            <div className="px-2 h-100" style={{ cursor: 'pointer' }} onClick={() => { setquantity(quantity - 50) }}>-</div>
                            <div className="px-1">
                                {quantity}
                            </div>
                            <div className="px-2 h-100" style={{ cursor: 'pointer' }} onClick={() => { setquantity(quantity + 50) }}>+</div>
                        </div>
                    </div>
                    <div>
                        {toppings.map(({top,price,number})=>{
                            
                        })}
                        meat <input type="checkbox" name="" id="" /> <br/>
                        Egg <input type="checkbox" name="" id=""/> <br/>
                        Kpomo <input type="checkbox" name="" id=""/> <br/>
                        Round fish <br/>
                        Titus fish
                    </div>
                    <div style={{ width: "100%", textAlign: "center" }}>
                        <button className="btn border" onClick={() => { submit() }}> Place Order</button>
                    </div>
                </div>
            </div>
        </React.Fragment>
    );
}
export default Order;
