import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import Navbar from '../Navbar/Navbar';

function Confirm(props) {
    const [Total, setTotal] = useState("")

    let x  ;
    const sendItem = useSelector(state => state.sendItem)
    return (
        <React.Fragment>
            <Navbar />
            <div className="h2 text-center w-100">Order List</div>
            <div className="border col-lg-4 p-3">
                <div className="">Buyer : {sendItem.Name}</div>
                <div className="">Phone : {sendItem.Phone}</div>
                <div className="">Room : {sendItem.Room}</div>
                <div className="">Hall : {sendItem.Hall}</div>
                <div className="">Food : {sendItem.Food} &#x20A6;{sendItem.Price}</div>
                <div className="">Date : {sendItem.Date || "You didn't add anything on top"}</div>
                <div className="">Things on top : {sendItem.addons !== undefined ? sendItem.addons.map(({top, price, number, newprice})=>{
                         x  = x + parseInt(newprice);
                        return (
                            <React.Fragment>
                            <span>{number}</span>  <span>{top}</span> <span>{newprice} {x} </span> ,
                            </React.Fragment>
                        )
                }) : "Error Sending topings"}</div>

            </div>
        </React.Fragment>
    );
}

export default Confirm;
