import React, { useState } from 'react';
import './style.css'
import Navbar from '../Navbar/Navbar';

function Order(props) {
    const [state, setstate] = useState({})
    var current = new Date();
    const writ = (e) => {
        setstate(state => ({ ...state, [e.target.name]: e.target.value }))
    }
    const submit =()=>{
        alert(current.toLocaleDateString())
        let keys = ['Name','Phone', 'Food', 'Hall']
    keys.forEach(element => {
        if (state[element] === '' ) {
            alert("fill")
        }
    });
        fetch('http://localhost:3000/order',{
            method: "post",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ state: state })
        })
        .then(res => res.json())
        .then(res => alert(res.data))
    }
    return (
        <React.Fragment>
            <Navbar />
            <div className="container position-relative" style={{ top: "20vh" }}>
                <div action="" className="col-lg-6 m-auto p-2 border">
                    <div className="h3 text-center">Complete this form to place your order </div>
                    <div className="">
                        Make orders with us and get your order under 1 hour
                    </div>
                    <div className="">
                        <input type="text" className="input my-2 rounded py-1 px-2 border" placeholder="Name" value={state.Name} name="Name" onChange={(e) => { writ(e) }} />
                    </div>
                    <div className="">
                        <input type="tel" className="input my-2 rounded py-1 px-2 border" placeholder="Phone" value={state.Phone} name="Phone" onChange={(e) => { writ(e) }} />
                    </div>
                    <div className="">
                        <input type="text" className="input my-2 rounded py-1 px-2 border" placeholder="Food" value={state.Food} name="Food" onChange={(e) => { writ(e) }} />
                    </div>
                    <select placeholder="Hall" value={state.Hall} name="Hall" className="input-s my-2" >
                        <option value="">--Select hall--</option>
                        <option value="Caleb Hall">Caleb Hall</option>
                        <option value="Joseph Hall">Joseph Hall</option>
                        <option value="Mary Hall">Mary Hall</option>
                        <option value="Rebecca Hall">Rebecca Hall</option>
                    </select>
                    <div style={{ width: "100%", textAlign: "center" }}>
                        <button className="btn border" onClick={()=>{submit()}}> Place Order</button>
                    </div>
                </div>
            </div>
        </React.Fragment>
    );
}
export default Order;
